
public class SwitchCase {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
			switch (4) {
			case 1:
			case 3:
			case 5:
			case 7:
			case 9:{
				System.out.printf("odd number");
				break;
			}
			case 0:
			case 2:
			case 4:
			case 6:
			case 8:
			case 10:{
				System.out.printf("even number\n");
				break;
			}
		}
			switch (5) {
			case 1:{
				System.out.printf("1");
				break;
			}
			case 2:{
				System.out.printf("2");
				break;
			}
			case 3:{
				System.out.printf("3");
				break;
			}
			case 4:{
				System.out.printf("4");
				break;
			}
			case 5:{
				System.out.printf("5");
				break;
			}
			case 6:{
				System.out.printf("6");
				break;
			}
			case 7:{
				System.out.printf("7");
				break;
			}
			case 8:{
				System.out.printf("8");
				break;
			}
			case 9:{
				System.out.printf("9");
				break;
			}
			case 10:{
				System.out.printf("10");
				break;
			}
		}
	}
}
